import vImage from "v-lazy-image"

export default {
    name: "adminReport",

    data: () => ({
        reports: [],
        report: null,
        pages: null,
        page: 1,
        report: {}
    }),
    methods: {

        async getReports(page) {


            let res = await this.$axios.get("/report/all/"+page, this.$headers)
            this.pages = res.data.pages
            this.page = res.data.page
            this.reports = res.data.reports
            console.log(res.data)

        },

        red(attended) {
            if(attended) return ""
            return "red"
        },

        stroke(id) {

            if(confirm("Are you sure you want to stroke user?")) {
                    
                let loader = this.$loading.show()

                this.$axios.put("/user/stroke/"+id,{}, {headers: {Authorization: `Bearer ${this.$jsCookie.getJSON('4u7h3n71c4710n').token}`}})
                .then(res => {

                    loader.hide()

                    if(res.data.error) {
                        this.$toasted.error(res.data.message, {icon: "times-circle"})
                    } else {
                        this.$toasted.success(res.data.message, {icon: "check"})            
                    }

                    this.getReports(this.page)
                    this.attended()                    

                })
                .catch(err => {
                    loader.hide()
                    this.$toasted.error(err, {icon: "times-circle"})
                })

            }


        },

        attended() {

            let loader = this.$loading.show()

            this.$axios.post("/report/attend/"+this.report._id, {}, this.$headers)
                .then(res => {
                    loader.hide()

                    if(res.data.error) {
                        this.$toasted.error(res.data.message, {icon: "times-circle"})
                    } else {
                        this.$toasted.success(res.data.message, {icon: "check"})            
                    }

                    this.getReports(this.page)
                    //this.attended()                    

                })
                .catch(err => {
                    loader.hide()
                    this.$toasted.error(err, {icon: "times-circle"})
                })
                
        }

    },
    created() {
        this.getReports(1)
    }
}
